mapper.update:
UPDATE rehugi
SET step = step + 1
WHERE ref = #{ref} AND step >= #{step}

mapper.insert:
SELECT COALESCE(MAX(step), 0) + 1 FROM rehugi WHERE ref = #{ref}
SELECT COALESCE(MAX(step), 0) + 1 FROM rehugi
INSERT INTO rehugi (rhnum, hnum, unum, rhcontent, rhwriteday, ref, step, depth) VALUES (#{rhnum}, #{hnum}, #{unum}, #{rhcontent}, now(), #{ref}, #{step}, #{depth})
